import{_ as a,o as l,c as e,av as i}from"./chunks/framework.UJgUSOgM.js";const u=JSON.parse('{"title":"微信小程序接口配置 https 后请求异常","description":"","frontmatter":{"title":"微信小程序接口配置 https 后请求异常"},"headers":[],"relativePath":"memo/web/applet-https.md","filePath":"zhHans/memo/web/applet-https.md","lastUpdated":1772171675000}'),s={name:"memo/web/applet-https.md"};function p(o,t,n,r,c,h){return l(),e("div",null,[...t[0]||(t[0]=[i('<h1 id="微信小程序接口配置-https-后请求异常" tabindex="-1">微信小程序接口配置 https 后请求异常 <a class="header-anchor" href="#微信小程序接口配置-https-后请求异常" aria-label="Permalink to &quot;微信小程序接口配置 https 后请求异常&quot;">​</a></h1><blockquote><p>具体表现为微信开发者工具关闭或开启域名校验都可以访问 真机调试情况下无论如何无法正常请求</p></blockquote><h2 id="问题原因" tabindex="-1">问题原因 <a class="header-anchor" href="#问题原因" aria-label="Permalink to &quot;问题原因&quot;">​</a></h2><p>常见于没有达到以下要求:</p><ul><li>HTTPS 证书必须有效 <ul><li>证书必须被系统信任，即根证书被已系统内置</li><li>部署 SSL 证书的网站域名必须与证书颁发的域名一致</li><li>证书必须在有效期内</li><li>证书的信任链必需完整（需要服务器配置）</li></ul></li><li>iOS 不支持自签名证书 <ul><li>iOS 下证书必须满足苹果 App Transport Security (ATS) 的要求</li><li>TLS 必须支持 1.2 及以上版本。部分旧 Android 机型还未支持 TLS 1.2，请确保 HTTPS 服务器的 TLS 版本支持 1.2 及以下版本</li><li>部分 CA 可能不被操作系统信任，请开发者在选择证书时注意小程序和各系统的相关通告。Chrome 56/57 内核对 WoSign、StartCom 证书限制周知</li></ul></li></ul><h2 id="解决办法" tabindex="-1">解决办法 <a class="header-anchor" href="#解决办法" aria-label="Permalink to &quot;解决办法&quot;">​</a></h2><ul><li>证书有效性 可以使用 openssl s_client -connect example.com:443 命令验证，也可以使用其他<a href="https://myssl.com/ssl.html" target="_blank" rel="noreferrer">在线工具</a></li><li>一般常见于证书的信任链不完整由后端解决</li><li>需要配置符合 PFS 规范的加密套件，推荐配置：</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ECDHE-RSA-AES128-GCM-SHA256:ECDHE:ECDH:AES:HIGH:!NULL:!aNULL:!MD5:!ADH:!RC4:!DH:!DHE</span></span></code></pre></div><ul><li>需要在服务端 TLS 协议中启用 TLS1.2，推荐配置：</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>TLSv1 TLSv1.1 TLSv1.2</span></span></code></pre></div><ul><li>需要保证当前域名与所使用的证书匹配</li><li>需要保证证书在有效期内</li><li>需要使用 SHA-2 签名算法的证书</li><li>需要保证证书签发机构是可信的 CA 机构</li><li>HSTS（HTTP 严格传输安全）的 max-age 需要大于 15768000 秒</li></ul>',11)])])}const S=a(s,[["render",p]]);export{u as __pageData,S as default};
